const mysql = require('mysql2');
const express = require('express');
const cors = require('cors');
const port = 4000;
const app = express();
app.use(cors());

// Configurar la conexión a la base de datos MySQL
const connection = mysql.createConnection({
  host: '34.67.253.112',
  port: '3306',
  user: 'raul',
  password: 'admin123',
  database: 'depa_w'
});

// Conectar a la base de datos MySQL
connection.connect((err) => {
  if (err) {
    console.error('Error al conectar a la base de datos:', err);
    return;
  }
  console.log('Conexión a la base de datos establecida');
});

// Ruta para obtener los datos de la base de datos de los empleados
app.get('/TraerEmpleado', (req, res) => {
  const query = `SELECT CONCAT(e.rut, '-', e.dv) AS rut_dv,
                  CONCAT(e.primer_nombre, ' ', e.segundo_nombre) AS nombres,
                  CONCAT(e.primer_apellido, ' ', e.segundo_apellido) AS apellidos,
                  e.mail AS correo,
                  c.cargo,
                  e.direccion,
                  co.nombre_comuna
                FROM Empleado e
                JOIN Cargo c ON e.id_cargo = c.id_cargo
                JOIN Comuna co ON e.id_comuna = co.id_comuna`;

  connection.query(query, (err, results) => {
    if (err) {
      console.error('Error al realizar la consulta:', err);
      res.status(500).json({ error: 'Error al obtener los datos' });
      return;
    }

    res.json(results);
  });
});

// Ruta para agregar un empleado a la base de datos
app.post('/AgregarEmpleado', (req, res) => {
  const { rut, nombre, apellido, direccion, correo, cargo, comuna } = req.body;
  const query = 'INSERT INTO Empleado (rut, dv, primer_nombre, segundo_nombre, primer_apellido, segundo_apellido, direccion, mail, estatus, id_cargo, usuario, id_conjunto, id_comuna, id_region) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?)';
  const values = [rut, '1', nombre, '', apellido, '', direccion, correo, 1, cargo, '', 1, comuna, 1];
  
  connection.query(query, values, (err, results) => {
    if (err) {
      console.error('Error al agregar el empleado:', err);
      res.status(500).json({ error: 'Error al agregar el empleado' });
      return;
    }
    res.json({ message: 'Empleado agregado correctamente' });
  });
});


// Escuchar en el puerto 3000
